import { useEffect, useState } from 'react';
import pagesService from '../services/pages';

const dummyPage = {
	_id: 123,
	name: "test page",
	thumbnail: "test thumbnail"
}


export const usePages = () => {
	const [pages, setPages] = useState([dummyPage]);
	useEffect(() => {
		pagesService.fetchPages().then(data => {
			console.log(data);
		});
	}, []);

	return { pages };
};
