import React, { useEffect, useState } from 'react';
import accountService from '../services/account';

export const AuthContext = React.createContext();

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    (async () => {
      const localUser = JSON.parse(localStorage.getItem('user'));

      console.log('HERE', localUser);
      if (localUser && localUser.id) {
        const user = await accountService.ApiGetUser(localUser.id);

        if (user) {
          localStorage.setItem(
            'user',
            JSON.stringify({ id: user._id, ...user.profile })
          );
          setCurrentUser(user);
        }
      }
    })();
  }, []);

  const auth = (userData) => {
    const user = { id: userData._id, ...userData.profile };
    localStorage.setItem('user', JSON.stringify(user));
    setCurrentUser(userData);
  };

  const signOut = () => {
    localStorage.removeItem('user');
    setCurrentUser(null);
  };

  useEffect(() => {
    if (currentUser) {
      localStorage.setItem('user', JSON.stringify(currentUser));
    } else {
      localStorage.removeItem('user');
    }
  }, [currentUser]);

  return (
    <AuthContext.Provider value={{ currentUser, auth, signOut }}>
      {children}
    </AuthContext.Provider>
  );
};
