import React from 'react';
import {useState} from 'react';

export default function PublishModal() {
  const [showModal, setShowModal] = useState(false);
const [linkInput, setLinkinput] =useState({
    link:'',
    folder:'',
});


const linkHandler=(e) => {

}
const publishHandler =({link, folder}) => {

